pragma solidity ^0.4.0;


contract Record {
    //access control
    address public owner = msg.sender;
    mapping (address => bool) private allowed;
    mapping (uint256 => Records) public data;

    modifier onlyBy(address _account)
    {
        require(
            msg.sender == _account,
            "Sender not authorized."
        );
        _;
    }
    modifier hasAccess {

        require(
        allowed[msg.sender] == true,
            "Sender not authorized."
        );
        _;
    }
    //data structure
    struct Records {
        string payload;
        string signature;
    }

    //data
    uint[16] public records;//testing -todo replace


    function modifyAccess(address _usrAddress , bool _permission) public onlyBy(owner) {

        allowed[_usrAddress] = _permission;

    }



    function addRecord(uint _record , uint _index) public hasAccess {
        records[_index] = _record;
    }

    function getRecord(uint index) public view returns (uint) {
        return records[index];
    }

}
